import React from 'react';
import {Alert} from "react-bootstrap"
import './Signin.css'
class Signin extends React.Component {

  state = {
    signInEmail:"",
    signInPassword: "",
    visible:false
  }

  onShowAlert = ()=>{
    this.setState({visible:true},()=>{
      window.setTimeout(()=>{
        this.setState({visible:false})
      },4000)
    });
  }

  onEmailChange = (event) => {
    this.setState({
      signInEmail:event.target.value
    })
  }

  onPasswordChange = (event) =>{
    this.setState({
      signInPassword:event.target.value
    })
  }

  onSubmitSignIn = () =>{
    fetch('https://desolate-waters-84729.herokuapp.com/signin', {
      method:'post',
      headers:{'Content-Type' : 'application/json'},
      body:JSON.stringify({
        email:this.state.signInEmail,
        password:this.state.signInPassword
      })
    })
      .then(response => response.json())
      .then(user => {

        if(user.id){
          this.props.loadUser(user)
          this.props.onRouteChange('home')
        }
        else{
          this.onShowAlert()
        }
      })


  }


  render() {

    return (
      <div>
      <article className="br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
        <main className="pa4 black-80">
          <div className="measure">
            <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
              <legend className="title">Sign In</legend>
              <div className="">
                <label htmlFor="email-address">Email</label>
                <input
                  onChange={this.onEmailChange}
                  className="transparent"
                  type="email"
                  name="email-address"
                  id="email-address"

                />
              </div>
              <div className="mv3">
                <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                <input
                  onChange={this.onPasswordChange}
                  className="transparent"
                  type="password"
                  name="password"
                  id="password"

                />
              </div>
            </fieldset>
            <div className="">
              <input
                onClick ={this.onSubmitSignIn}
                className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib"
                type="submit"
                value="Sign in"
              />
            </div>
            <div className="lh-copy mt3">
              <p onClick={() => this.props.onRouteChange('register')}  className="f6 link dim black db pointer">Register</p>
            </div>
          </div>
        </main>
      </article>
      <Alert variant="info" show={this.state.visible} >
         Wrong Username or Password
      </Alert>
      </div>
    );
  }
}

export default Signin;
